package edu.demo.cqrs.controller; import edu.demo.cqrs.model.Item; import edu.demo.cqrs.repo.ItemRepo; import org.springframework.web.bind.annotation.*; import java.util.List; @RestController @RequestMapping("/items") public class CqrsController { private final ItemRepo repo; public CqrsController(ItemRepo r){this.repo=r;} @PostMapping public Item create(@RequestBody Item item){ return repo.save(item); } @GetMapping public List<Item> list(){ return repo.findAll(); } }
