package edu.demo.cqrs.model; import javax.persistence.Entity; import javax.persistence.GeneratedValue; import javax.persistence.Id; @Entity public class Item { @Id @GeneratedValue private Long id; private String name; public Item(){} public Item(String n){this.name=n;} public Long getId(){return id;} public String getName(){return name;} public void setName(String n){this.name=n;} }
