package edu.demo.dbper.controller; import edu.demo.dbper.model.Product; import edu.demo.dbper.repo.ProductRepo; import org.springframework.web.bind.annotation.*; import java.util.List; @RestController @RequestMapping("/products") public class ProductController { private final ProductRepo repo; public ProductController(ProductRepo r){this.repo=r;} @PostMapping public Product create(@RequestBody Product p){ return repo.save(p); } @GetMapping public List<Product> all(){ return repo.findAll(); } }
