package edu.demo.dbper.model; import javax.persistence.Entity; import javax.persistence.GeneratedValue; import javax.persistence.Id; @Entity public class Product { @Id @GeneratedValue private Long id; private String name; public Product(){} public Product(String n){this.name=n;} public Long getId(){return id;} public String getName(){return name;} public void setName(String n){this.name=n;} }
